package zeta.foods.service.impl;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseAuthException;
import com.google.firebase.auth.UserRecord;
import com.google.firebase.auth.UserRecord.CreateRequest;
import zeta.foods.service.FirebaseAuthService;

import java.util.HashMap;
import java.util.Map;

public class FirebaseAuthServiceImpl implements FirebaseAuthService {

    private static final String ADMIN_EMAIL = "ambatisaiteja123@gmail.com";

    // Predefined waiter accounts
    private static final Map<String, String> WAITERS = new HashMap<>();

    static {
        // Initialize waiters with email and password "12345678"
        WAITERS.put("waiter1@restaurant.com", "12345678");
        WAITERS.put("waiter2@restaurant.com", "12345678");
        WAITERS.put("waiter3@restaurant.com", "12345678");
        WAITERS.put("waiter4@restaurant.com", "12345678");
        WAITERS.put("waiter5@restaurant.com", "12345678");
    }

    @Override
    public UserRecord authenticateUser(String email, String password) {
        try {
            // For admin authentication
            if (email.equals(ADMIN_EMAIL) || WAITERS.containsKey(email)) {
                // In real implementation, this would verify the password with Firebase
                // Here we're just checking if it's a known user
                return FirebaseAuth.getInstance().getUserByEmail(email);
            }
            return null;
        } catch (FirebaseAuthException e) {
            System.err.println("Authentication failed: " + e.getMessage());
            return null;
        }
    }

    @Override
    public boolean isAdmin(String email) {
        return email.equals(ADMIN_EMAIL);
    }

    @Override
    public boolean isWaiter(String email) {
        return WAITERS.containsKey(email);
    }

    @Override
    public UserRecord createUser(String email, String password, boolean isAdmin) {
        CreateRequest request = new CreateRequest()
                .setEmail(email)
                .setPassword(password)
                .setEmailVerified(true);

        try {
            UserRecord userRecord = FirebaseAuth.getInstance().createUser(request);

            // Add custom claims for role-based access
            Map<String, Object> claims = new HashMap<>();
            claims.put("admin", isAdmin);
            claims.put("waiter", !isAdmin);

            FirebaseAuth.getInstance().setCustomUserClaims(userRecord.getUid(), claims);

            return userRecord;
        } catch (FirebaseAuthException e) {
            System.err.println("Error creating user: " + e.getMessage());
            return null;
        }
    }

    // Method to create predefined users if they don't exist
    public void setupPredefinedUsers() {
        try {
            // Try to create admin
            try {
                FirebaseAuth.getInstance().getUserByEmail(ADMIN_EMAIL);
            } catch (FirebaseAuthException e) {
                // User doesn't exist, create it
                createUser(ADMIN_EMAIL, "admin123", true);
                System.out.println("Created admin user: " + ADMIN_EMAIL);
            }

            // Create waiter accounts
            for (String waiterEmail : WAITERS.keySet()) {
                try {
                    FirebaseAuth.getInstance().getUserByEmail(waiterEmail);
                } catch (FirebaseAuthException e) {
                    // User doesn't exist, create it
                    createUser(waiterEmail, WAITERS.get(waiterEmail), false);
                    System.out.println("Created waiter user: " + waiterEmail);
                }
            }
        } catch (Exception e) {
            System.err.println("Error setting up predefined users: " + e.getMessage());
        }
    }
}

