package zeta.foods.simulation;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import zeta.foods.model.User;
import zeta.foods.service.AuthService;
import zeta.foods.service.impl.PostgresAuthServiceImpl;

/**
 * A simulation tool for testing the authentication system
 * without requiring manual input.
 */
public class AuthSimulation {
    private static final Logger logger = LoggerFactory.getLogger(AuthSimulation.class);
    private final AuthService authService;

    public AuthSimulation(AuthService authService) {
        this.authService = authService;
    }

    /**
     * Run a simulation to test user login with different account types.
     */
    public void runLoginSimulation() {
        logger.info("=== Starting Authentication Simulation ===");

        // Test admin login
        logger.info("Testing admin login...");
        testLogin("ambatisaiteja123@gmail.com", "admin123", "Admin");

        // Test waiter login
        logger.info("Testing waiter login...");
        testLogin("waiter1@restaurant.com", "12345678", "Waiter");

        // Test customer login
        logger.info("Testing customer login...");
        testLogin("customer1@example.com", "12345678", "Customer");

        // Test invalid login
        logger.info("Testing invalid login...");
        testLogin("nonexistent@example.com", "wrongpassword", "Invalid User");

        logger.info("=== Authentication Simulation Completed ===");
    }

    /**
     * Helper method to test a specific login
     */
    private void testLogin(String email, String password, String userType) {
        logger.info("Attempting to log in as {}: {}", userType, email);
        User user = authService.authenticateUser(email, password);

        if (user != null) {
            logger.info(" Login successful for {}", userType);
            logger.info("User details - ID: {}, Username: {}, Role: {}", 
                    user.getId(), user.getUsername(), user.getRole());

            // Display appropriate menu based on user role
            if (user.isAdmin()) {
                displayAdminMenu();
            } else if (user.isWaiter()) {
                displayWaiterMenu();
            } else {
                displayCustomerMenu();
            }

            logger.info("Session ended for {}", userType);
        } else {
            logger.info(" Login failed for {} with email: {}", userType, email);
        }

        logger.info("-----------------------------------");
    }

    /**
     * Display admin menu options
     */
    private void displayAdminMenu() {
        logger.info(" ADMIN MENU:");
        logger.info("1. Manage Users");
        logger.info("2. View System Reports");
        logger.info("3. Update Inventory");
        logger.info("4. Manage Menu Items");
        logger.info("5. Exit");

        // Simulate admin selecting an option
        logger.info("Admin selected: View System Reports");
        logger.info("Generating reports...");
        logger.info("Total Orders: 243");
        logger.info("Revenue: $5,678.90");
        logger.info("Popular Items: Pasta Carbonara, Chicken Parmesan");
    }

    /**
     * Display waiter menu options
     */
    private void displayWaiterMenu() {
        logger.info(" WAITER MENU:");
        logger.info("1. Take New Order");
        logger.info("2. View Table Status");
        logger.info("3. Process Payment");
        logger.info("4. Exit");

        // Simulate waiter selecting an option
        logger.info("Waiter selected: View Table Status");
        logger.info("Table 1: Occupied (4 guests) - Order in progress");
        logger.info("Table 2: Available");
        logger.info("Table 3: Reserved (19:00)");
        logger.info("Table 4: Occupied (2 guests) - Waiting for food");
    }

    /**
     * Display customer menu options
     */
    private void displayCustomerMenu() {
        logger.info(" CUSTOMER MENU:");
        logger.info("1. View Menu");
        logger.info("2. Place Order");
        logger.info("3. View Order Status");
        logger.info("4. Exit");

        // Simulate customer selecting an option
        logger.info("Customer selected: View Menu");
        logger.info("APPETIZERS:");
        logger.info("- Garlic Bread ($4.99)");
        logger.info("- Calamari ($8.99)");
        logger.info("MAIN COURSES:");
        logger.info("- Pasta Carbonara ($14.99)");
        logger.info("- Grilled Salmon ($19.99)");
    }
}

package zeta.foods.utils;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.SQLException;

public class DatabaseUtil {
    private static final Logger logger = LoggerFactory.getLogger(DatabaseUtil.class);

    private static final String DB_HOST = System.getenv().getOrDefault("DB_HOST", "localhost");
    private static final String DB_PORT = System.getenv().getOrDefault("DB_PORT", "5432");
    private static final String DB_NAME = System.getenv().getOrDefault("DB_NAME", "restaurant_management");
    private static final String DB_USER = System.getenv().getOrDefault("DB_USER", "postgres");
    private static final String DB_PASSWORD = System.getenv().getOrDefault("DB_PASSWORD", "postgres");

    private static final String JDBC_URL = String.format("jdbc:postgresql://%s:%s/%s", DB_HOST, DB_PORT, DB_NAME);

    private static Connection connection = null;

    public static Connection getConnection() throws SQLException {
        if (connection == null || connection.isClosed()) {
            try {
                // Load PostgreSQL JDBC driver
                Class.forName("org.postgresql.Driver");

                // Create connection
                connection = DriverManager.getConnection(
                    JDBC_URL,
                    DB_USER,
                    DB_PASSWORD
                );

                logger.info("Database connection established");
            } catch (ClassNotFoundException e) {
                logger.error("PostgreSQL JDBC driver not found", e);
                throw new SQLException("Database driver not found", e);
            } catch (SQLException e) {
                logger.error("Failed to connect to database: {}", e.getMessage(), e);
                throw e;
            }
        }

        return connection;
    }

    public static void closeConnection() {
        if (connection != null) {
            try {
                connection.close();
                logger.info("Database connection closed");
            } catch (SQLException e) {
                logger.error("Error closing database connection", e);
            }
        }
    }
}

package zeta.foods.utils;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.SQLException;

public class DatabaseUtil {
    private static final Logger logger = LoggerFactory.getLogger(DatabaseUtil.class);

    private static final String DB_HOST = System.getenv().getOrDefault("DB_HOST", "localhost");
    private static final String DB_PORT = System.getenv().getOrDefault("DB_PORT", "5432");
    // Check for new database from migrations, otherwise use default
    private static final String DB_NAME = System.getenv().getOrDefault("DB_NAME", "restaurant_management_new");
    private static final String DB_USER = System.getenv().getOrDefault("DB_USER", "restaurant_user");
    private static final String DB_PASSWORD = System.getenv().getOrDefault("DB_PASSWORD", "restaurant_password");

    private static final String JDBC_URL = String.format("jdbc:postgresql://%s:%s/%s", DB_HOST, DB_PORT, DB_NAME);

    private static Connection connection = null;

    public static Connection getConnection() throws SQLException {
        if (connection == null || connection.isClosed()) {
            try {
                // Load PostgreSQL JDBC driver
                Class.forName("org.postgresql.Driver");

                logger.info("Connecting to database {} at {}:{}", DB_NAME, DB_HOST, DB_PORT);

                // Create connection
                connection = DriverManager.getConnection(
                    JDBC_URL,
                    DB_USER,
                    DB_PASSWORD
                );

                logger.info("Database connection established");
            } catch (ClassNotFoundException e) {
                logger.error("PostgreSQL JDBC driver not found", e);
                throw new SQLException("Database driver not found", e);
            } catch (SQLException e) {
                logger.error("Failed to connect to database: {}", e.getMessage(), e);
                throw e;
            }
        }

        return connection;
    }

    public static void closeConnection() {
        if (connection != null) {
            try {
                connection.close();
                logger.info("Database connection closed");
            } catch (SQLException e) {
                logger.error("Error closing database connection", e);
            }
        }
    }
}

package zeta.foods.utils;

import java.io.*;
import java.nio.file.*;
import java.util.*;
import java.util.concurrent.ConcurrentHashMap;

public class InventoryManager {
    private static final String DEFAULT_INVENTORY_PATH = "src/main/resources/inventory.txt";
    private static final String CURRENT_INVENTORY_PATH = "src/main/resources/CurrentInventory.txt";

    // Map to store inventory items and quantities
    private static Map<String, Integer> currentInventory = new ConcurrentHashMap<>();
    private static Map<String, Integer> defaultInventory = new ConcurrentHashMap<>();

    static {
        // Load both default and current inventory on startup
        loadDefaultInventory();
        loadCurrentInventory();

        // If current inventory is empty, initialize it with default inventory
        if (currentInventory.isEmpty()) {
            currentInventory.putAll(defaultInventory);
            saveCurrentInventory();
        }
    }

    // Load default inventory from inventory.txt
    private static void loadDefaultInventory() {
        try {
            List<String> lines = Files.readAllLines(Paths.get(DEFAULT_INVENTORY_PATH));

            for (String line : lines) {
                if (line.trim().isEmpty() || line.startsWith("//")) {
                    continue;
                }

                String[] parts = line.split("-");
                if (parts.length == 2) {
                    String ingredient = parts[0].trim();
                    int quantity = Integer.parseInt(parts[1].trim());
                    defaultInventory.put(ingredient, quantity);
                }
            }

            System.out.println("Default inventory loaded successfully with " + defaultInventory.size() + " items.");
        } catch (IOException e) {
            System.err.println("Error loading default inventory: " + e.getMessage());
        }
    }

    // Load current inventory from CurrentInventory.txt
    private static void loadCurrentInventory() {
        try {
            Path currentInventoryPath = Paths.get(CURRENT_INVENTORY_PATH);

            if (!Files.exists(currentInventoryPath) || Files.size(currentInventoryPath) == 0) {
                System.out.println("Current inventory file is empty or doesn't exist. Will initialize with default inventory.");
                return;
            }

            List<String> lines = Files.readAllLines(currentInventoryPath);

            for (String line : lines) {
                if (line.trim().isEmpty() || line.startsWith("//")) {
                    continue;
                }

                String[] parts = line.split("-");
                if (parts.length == 2) {
                    String ingredient = parts[0].trim();
                    int quantity = Integer.parseInt(parts[1].trim());
                    currentInventory.put(ingredient, quantity);
                }
            }

            System.out.println("Current inventory loaded successfully with " + currentInventory.size() + " items.");
        } catch (IOException e) {
            System.err.println("Error loading current inventory: " + e.getMessage());
        }
    }

    // Save current inventory to CurrentInventory.txt
    public static void saveCurrentInventory() {
        try (BufferedWriter writer = new BufferedWriter(new FileWriter(CURRENT_INVENTORY_PATH))) {
            for (Map.Entry<String, Integer> entry : currentInventory.entrySet()) {
                writer.write(entry.getKey() + " - " + entry.getValue());
                writer.newLine();
            }
            System.out.println("Current inventory saved successfully.");
        } catch (IOException e) {
            System.err.println("Error saving current inventory: " + e.getMessage());
        }
    }

    // Get available quantity of an ingredient
    public static int getAvailableQuantity(String ingredient) {
        return currentInventory.getOrDefault(ingredient, 0);
    }

    // Check if ingredients are available in required quantities
    public static boolean checkIngredientsAvailability(Map<String, Integer> ingredients) {
        for (Map.Entry<String, Integer> entry : ingredients.entrySet()) {
            String ingredient = entry.getKey();
            int requiredQuantity = entry.getValue();

            if (getAvailableQuantity(ingredient) < requiredQuantity) {
                return false;
            }
        }
        return true;
    }

    // Update inventory after using ingredients
    public static boolean useIngredients(Map<String, Integer> ingredients) {
        // First check if all ingredients are available
        if (!checkIngredientsAvailability(ingredients)) {
            return false;
        }

        // If all are available, deduct them from inventory
        for (Map.Entry<String, Integer> entry : ingredients.entrySet()) {
            String ingredient = entry.getKey();
            int requiredQuantity = entry.getValue();

            int currentQuantity = currentInventory.getOrDefault(ingredient, 0);
            currentInventory.put(ingredient, currentQuantity - requiredQuantity);
        }

        // Save the updated inventory
        saveCurrentInventory();
        return true;
    }

    // Get list of missing ingredients
    public static List<String> getMissingIngredients(Map<String, Integer> ingredients) {
        List<String> missingIngredients = new ArrayList<>();

        for (Map.Entry<String, Integer> entry : ingredients.entrySet()) {
            String ingredient = entry.getKey();
            int requiredQuantity = entry.getValue();

            if (getAvailableQuantity(ingredient) < requiredQuantity) {
                missingIngredients.add(ingredient + " (Need: " + requiredQuantity + 
                                      ", Available: " + getAvailableQuantity(ingredient) + ")");
            }
        }

        return missingIngredients;
    }

    // Reset current inventory to default values
    public static void resetInventory() {
        currentInventory.clear();
        currentInventory.putAll(defaultInventory);
        saveCurrentInventory();
        System.out.println("Inventory reset to default values.");
    }

    // Get a copy of the current inventory
    public static Map<String, Integer> getCurrentInventory() {
        return new HashMap<>(currentInventory);
    }

    // Restock an item to its default quantity
    public static void restockItem(String ingredient) {
        if (defaultInventory.containsKey(ingredient)) {
            currentInventory.put(ingredient, defaultInventory.get(ingredient));
            saveCurrentInventory();
            System.out.println(ingredient + " restocked to default quantity: " + defaultInventory.get(ingredient));
        }
    }
}
