package zeta.foods.config;

import com.google.auth.oauth2.GoogleCredentials;
import com.google.firebase.FirebaseApp;
import com.google.firebase.FirebaseOptions;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public class FirebaseConfig {
    private static final Logger logger = LoggerFactory.getLogger(FirebaseConfig.class);

    public static void initializeFirebase() {
        try {
            if (FirebaseApp.getApps().isEmpty()) {
                logger.info("Initializing Firebase in test mode...");

                // For development/testing purposes, we'll initialize Firebase with empty credentials
                FirebaseOptions options = FirebaseOptions.builder()
                        .setCredentials(GoogleCredentials.fromAccessToken("test-token")) // Use dummy token for test mode
                        .setProjectId("login-4e5bf")
                        .setDatabaseUrl("https://login-4e5bf-default-rtdb.firebaseio.com")
                        .build();

                FirebaseApp.initializeApp(options);
                logger.info("Firebase initialized successfully in test mode");
            } else {
                logger.info("Firebase app already initialized");
            }
        } catch (Exception e) {
            logger.error("Error initializing Firebase: {}", e.getMessage(), e);
            throw new RuntimeException("Failed to initialize Firebase", e);
        }
    }
}

